import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-flows',
  templateUrl: './flows.component.html'
})
export class FlowsComponent {
  public flows: Flow[] = [];

  constructor(http: HttpClient, @Inject('API_URL') baseUrl: string) {
    http.get<Flow[]>(baseUrl + 'flows').subscribe(result => {
      this.flows = result;
    }, error => console.error(error));
  }
}

interface Flow {
  id: number,
  name: string
}
<h1 id="tableLabel">Flows</h1>

<p *ngIf="!flows?.length"><em>Loading...</em></p>

<table class="table table-striped" aria-labelledby="tableLabel" *ngIf="flows.length">
  <thead>
    <tr>
      <th>Id</th>
      <th>Name</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let flow of flows">
      <td>{{ flow.id }}</td>
      <td>{{ flow.name }}</td>
    </tr>
  </tbody>
</table>
