import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { ActivatedRoute } from '@angular/router';
import { empty } from 'rxjs';

@Component({
  selector: 'app-job',
  templateUrl: './job.component.html'
})
export class JobComponent {

  public job : any;

  constructor(http: HttpClient, @Inject('API_URL') apiUrl: string, private router: Router, private route: ActivatedRoute) {

    var flowId = this.route.snapshot.paramMap.get('flowId');
    http.get(apiUrl + `job/${flowId}`).subscribe(result => {
      this.job = result;
    })
    /*http.get<Job>(apiUrl + `job/${flowId}`).subscribe(result => {
      this.job = result;
    }, error => console.error(error));*/
  }
}

/*interface Job {
  id: number,
  flow: object,
  jobSteps: object
}*/
<p *ngIf="!job?.id">Loading Job...</p>

<h1>{{ job.flow.name }} </h1>

<div *ngFor="let jobStep of job.jobSteps">
  {{ jobStep.step.name }}
</div>
