import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-job',
  templateUrl: './job.component.html'
})
export class JobComponent {

  public job: Job = {};

  constructor(http: HttpClient, @Inject('API_URL') apiUrl: string, private router: Router, private route: ActivatedRoute) {

    var flowId = this.route.snapshot.paramMap.get('flowId');

    http.get<Job>(apiUrl + `job/${flowId}`).subscribe(result => {
      this.job = result;
    }, error => console.error(error));
  }
}

interface Job {

}
