import { Component, Input, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-evidence',
  templateUrl: './evidence.component.html',
  styleUrls: ['./evidence.component.css']
})
export class EvidenceComponent {

  @Input('evidence') evidence: any;  
  @Output() updated = new EventEmitter();

  update(event: any) {
    console.log(event.target.checked);
    this.updated.emit(this.evidence);
  }
}



<div *ngIf="evidence.evidenceType == 1">
  <div>{{ evidence.name }}</div>
  <div>
    <input type="text" (keyup)="update($event)" />
  </div>
</div>
<div *ngIf="evidence.evidenceType == 2">
  <div>{{ evidence.name }}</div>
  <div>
    <input type="checkbox" (change)="update($event)" />
  </div>
</div>
