import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-flows',
  templateUrl: './flows.component.html'
})
export class FlowsComponent {

  public flows: Flow[] = [];

  constructor(http: HttpClient, @Inject('API_URL') apiUrl: string, private router: Router) {
    http.get<Flow[]>(apiUrl + 'flows').subscribe(result => {
      this.flows = result;
    }, error => console.error(error));
  }

  public executeFlow(flowId: number) {
    this.router.navigate(['job', flowId]);
  }
}

interface Flow {
  id: number,
  name: string
}
