import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-flows',
  templateUrl: './flows.component.html'
})
export class FlowsComponent {

  public flows: Flow[] = [];
  public jobHistory: any;

  constructor(http: HttpClient, @Inject('API_URL') apiUrl: string, private router: Router) {
    http.get<Flow[]>(apiUrl + 'flows').subscribe(result => {
      this.flows = result;
    }, error => console.error(error));
    http.get<any>(apiUrl + 'job/history').subscribe(result => {
      this.jobHistory = result;
    }, error => console.error(error));
  }
}

interface Flow {
  id: number,
  name: string
}
<h1 id="tableLabel">Flows</h1>

<p *ngIf="!flows?.length"><em>Loading...</em></p>

<table class="table table-striped" aria-labelledby="tableLabel" *ngIf="flows.length">
  <thead>
    <tr>
      <th>Id</th>
      <th>Name</th>
      <th></th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let flow of flows">
      <td>{{ flow.id }}</td>
      <td>{{ flow.name }}</td>
      <td><a class="nav-link text-dark" [routerLink]="['/job', flow.id]">Execute</a></td>
    </tr>
  </tbody>
</table>


<h1>Job History</h1>

<table class="table table-striped" aria-labelledby="tableLabel" *ngIf="flows.length">
  <thead>
    <tr>
      <th>Job Id</th>
      <th>Flow Name</th>
      <th>Completed On</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let job of jobHistory">
      <td>{{ job.id }}</td>
      <td>{{ flow.name }}</td>
      <td><a class="nav-link text-dark" [routerLink]="['/job', flow.id]">Execute</a></td>
    </tr>
  </tbody>
</table>
