import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { ActivatedRoute } from '@angular/router';
import { empty } from 'rxjs';

@Component({
  selector: 'app-job',
  templateUrl: './job.component.html',
  styleUrls: ['./job.component.css']
})
export class JobComponent {

  public job: any;
  public jobStepIndex: number = 0;

  constructor(http: HttpClient, @Inject('API_URL') apiUrl: string, private router: Router, private route: ActivatedRoute) {

    var flowId = this.route.snapshot.paramMap.get('flowId');
    http.get(apiUrl + `job/${flowId}`).subscribe(result => {
      this.job = result;      
    })  
  }

  public doTransition(transition: any) {
    if (transition.isEnd) {
      this.jobStepIndex++;
    }
  }
}


<p *ngIf="!job?.id">Loading Job...</p>

<h1>{{ job.flow.name }} </h1>

<div class="step">
  <div>{{ job.jobSteps[jobStepIndex].step.name }}</div>  
</div>
