import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { ActivatedRoute } from '@angular/router';
import { empty } from 'rxjs';

@Component({
  selector: 'app-job',
  templateUrl: './job.component.html',
  styleUrls: ['./job.component.css']
})
export class JobComponent {

  public job: any;
  public jobStepIndex: number = 0;

  constructor(http: HttpClient, @Inject('API_URL') apiUrl: string, private router: Router, private route: ActivatedRoute) {
    
    var flowId = this.route.snapshot.paramMap.get('flowId');

    http.get(apiUrl + `job/${flowId}`).subscribe(result => {
      this.job = result;      
    })  
  }

  public updateEvidence(evidence: any, value: any) {
    console.log(evidence, value);
    evidence.value = value;
  }

  public doTransition(transition: any) {
    console.log(transition);
    if (!transition.isEnd) {
      this.jobStepIndex++;
      console.log(this.jobStepIndex);
    }
    else {
      this.router.navigate(['/flows']);
    }
  }

}


<p *ngIf="!job?.id">Loading Job...</p>

<h1>{{ job.flow.name }} </h1>

<div class="step">
  <div>{{ job.jobSteps[jobStepIndex].step.name }}</div>
  <div *ngFor="let evidence of job.jobSteps[jobStepIndex].evidence">
    <div>{{ evidence.name }}</div>
    <div *ngIf="evidence.evidenceType == 1">
      <input type="text"  />
    </div>
    <div *ngIf="evidence.evidenceType == 2">
      <input type="checkbox" />
    </div>
  </div>
</div>

<button *ngFor="let transition of job.jobSteps[jobStepIndex].transitions" class="btn btn-primary" (click)="doTransition(transition)">{{ transition.name }}</button>
