import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-job-history',
  templateUrl: './job-history.component.html',
  styleUrls: ['./job-history.component.css']
})
export class JobHistoryComponent {

  public job: any;

  constructor(http: HttpClient, @Inject('API_URL') apiUrl: string, private router: Router, private route: ActivatedRoute) {

    var jobId = this.route.snapshot.paramMap.get('jobId');

    http.get<any>(apiUrl + `job/history/${jobId}`).subscribe(result => {
      this.job = result;
    }, error => console.error(error));    
  }
}


<h1 id="tableLabel">Job History</h1>

<p *ngIf="!job?.id"><em>Loading...</em></p>

<h2>Job Id: {{ job.id }} (Flow: {{ job.flow.name }})</h2>

<div *ngFor="let jobStep of job.jobSteps" class="step">
  <div>{{ jobStep.step.name }}</div>
  <div *ngFor="let evidence of jobStep.evidence" class="evidence">
    <div>{{ evidence.name }} : {{ evidence.value }}</div>
  </div>
</div>

<button class="btn btn-primary" [routerLink]="['/flows']">Back</button>
